mapper.delete:
delete from replay_sku_flow_user_info
where 1=1

  and id = #{id,jdbcType=BIGINT}


  and sku_flow_id = #{skuFlowId,jdbcType=VARCHAR}


  and sku_id = #{skuId,jdbcType=VARCHAR}


  and user_id = #{userId,jdbcType=VARCHAR}


  and sku_flow_date = #{skuFlowDate,jdbcType=DATE}


  and send_time = #{sendTime,jdbcType=TIMESTAMP}


  and receive_time = #{receiveTime,jdbcType=TIMESTAMP}

mapper.select:
select a.sku_id,totalUv from (
  select sku_id,count(distinct user_id) totalUv
  from replay_sku_flow_user_info


       and sku_flow_date >= #{begDate}


      and sku_flow_date <= #{endDate}


      and sku_id in

        #{item}



 GROUP BY sku_id
) a where a.sku_id is not null

select a.erp_goods_info_no as id,totalUv from (
select erp_goods_info_no,count(distinct user_id) totalUv
from replay_sku_flow_user_info a join replay_goods_info b on a.sku_id = b.goods_info_id


    and sku_flow_date = date_format(#{begDate},'%Y-%m-%d')


    and sku_flow_date >= #{begDate}
    and sku_flow_date <= #{endDate}


    and b.erp_goods_info_no in

      #{item}



GROUP BY b.erp_goods_info_no
) a where a.erp_goods_info_no is not null
