mapper.select:
select
from replay_store_customer_rela a inner JOIN replay_customer b ON a.customer_id = b.customer_id AND b.check_state = 1 WHERE a.employee_id is not null AND a.employee_id != ''
and a.create_time between #{startTime} and #{endTime}
and a.employee_id = #{employeeId}
GROUP BY a.employee_id

SELECT c.employee_id, count(1) as total FROM replay_customer a INNER JOIN replay_customer_detail b ON a.customer_id = b.customer_id
and a.create_time BETWEEN #{startTime} and #{endTime}
INNER JOIN replay_employee c ON b.employee_id = c.employee_id WHERE c.account_type = 1
and c.employee_id = #{employeeId}
GROUP BY c.employee_id
