mapper.select:
select
  i.sku_id id,count(distinct t.customer_id) customerCount
from replay_trade t
join replay_trade_item i on(t.tid=i.tid)


        and t.create_time >= #{begDate}


        and t.create_time <= #{endDate}


        and i.sku_id in

            #{item}



group by i.sku_id

select id, orderCount, orderAmt, orderNum, payCount, payNum, payAmt, returnOrderCount, returnOrderAmt, returnOrderNum, a.goods_info_name name from ( SELECT kk.erp_goods_info_no as id, kk.goods_info_name as goods_info_name, sum(ORDER_COUNT) AS orderCount, sum(ORDER_MONEY) AS orderAmt, sum(ORDER_NUM) AS orderNum, sum(PAY_COUNT) AS payCount, sum(PAY_NUM) AS payNum, sum(PAY_MONEY) AS payAmt, sum(REFUND_COUNT) AS returnOrderCount, sum(REFUND_MONEY) AS returnOrderAmt, sum(REFUND_NUM) AS returnOrderNum FROM (select b.*,a.erp_goods_info_no,a.goods_info_name from replay_goods_info a join goods_day b on(a.goods_info_id=b.goods_info_id) where b.STAT_DATE >= date_format(#{begDate},'%Y-%m-%d') AND b.STAT_DATE <= date_format(#{endDate},'%Y-%m-%d')
AND b.SHOP_ID = #{companyId}
) kk GROUP BY kk.erp_goods_info_no, kk.goods_info_name ORDER BY orderNum DESC ) a
limit #{number}, #{size}

select goods_info_id id, erp_goods_info_no erpGoodsInfoNo, goods_info_name goodsInfoName, goods_id goodsId, goods_info_no goodsInfoNo, customer_id customerId, company_info_id companyId, store_id storeId, brand_id brandId from replay_goods_info
and GOODS_INFO_ID in
#{item}
and (goods_info_name like '%${keyWord}%' or goods_info_no like '%${keyWord}%')
and brand_id = #{brandId}
limit ${pageNum},${pageSize}

select a.goods_info_id id, a.erp_goods_info_no erpGoodsInfoNo, a.goods_info_name goodsInfoName, a.goods_id goodsId, a.goods_info_no goodsInfoNo, a.customer_id customerId, a.company_info_id companyId, a.store_id storeId, a.brand_id brandId from replay_goods_info a join ${table} b on(a.goods_info_id=b.goods_info_id)
and (a.goods_info_name like '%${keyWord}%' or a.goods_info_no like '%${keyWord}%')
and b.shop_id = #{companyId}
AND b.STAT_DATE = date_format(#{date},'%Y-%m-%d')
AND b.STAT_MONTH = #{month}
AND b.STAT_YEAR = #{year}
limit #{number}, #{size}

select id, orderCount, orderAmt, orderNum, payCount, payNum, payAmt, returnOrderCount, returnOrderAmt, returnOrderNum, a.goods_info_name name from ( SELECT kk.erp_goods_info_no as id, kk.goods_info_name as goods_info_name, sum(ORDER_COUNT) AS orderCount, sum(ORDER_MONEY) AS orderAmt, sum(ORDER_NUM) AS orderNum, sum(PAY_COUNT) AS payCount, sum(PAY_NUM) AS payNum, sum(PAY_MONEY) AS payAmt, sum(REFUND_COUNT) AS returnOrderCount, sum(REFUND_MONEY) AS returnOrderAmt, sum(REFUND_NUM) AS returnOrderNum FROM (select b.*,a.erp_goods_info_no,a.goods_info_name from replay_goods_info a join ${table} b on(a.goods_info_id=b.goods_info_id)
and (a.goods_info_name like '%${keyWord}%' or a.goods_info_no like '%${keyWord}%')
and b.shop_id = #{companyId}
AND b.STAT_DATE = date_format(#{date},'%Y-%m-%d')
AND b.STAT_MONTH = #{month}
AND b.STAT_YEAR = #{year}
) kk GROUP BY kk.erp_goods_info_no, kk.goods_info_name ORDER BY orderNum DESC ) a
limit #{number}, #{size}

