mapper.select:
        SELECT

        FROM live_room_rela


                and live_room_rela_id = #{liveRoomRelaId,jdbcType=BIGINT}


                and live_room_id = #{liveRoomId,jdbcType=BIGINT}


                and rela_type = #{relaType,jdbcType=VARCHAR}


                and rela_id = #{relaId,jdbcType=VARCHAR}


                and rela_content = #{relaContent,jdbcType=VARCHAR}



                and status = #{status,jdbcType=BIGINT}


                and create_person = #{createPerson,jdbcType=BIGINT}


                and create_time = #{createTime,jdbcType=TIMESTAMP}


                and update_person = #{updatePerson,jdbcType=BIGINT}


                and update_time = #{updateTime,jdbcType=TIMESTAMP}


                and del_flag = #{delFlag,jdbcType=BIGINT}



                and live_room_id in

				 #{item, jdbcType=BIGINT}



        order by live_room_rela_id desc

        LIMIT #{pageNum,jdbcType=BIGINT},#{pageSize,jdbcType=BIGINT}

select count(1) FROM live_room_rela


        and live_room_rela_id = #{liveRoomRelaId,jdbcType=BIGINT}


        and live_room_id = #{liveRoomId,jdbcType=BIGINT}


        and rela_type = #{relaType,jdbcType=VARCHAR}


        and rela_id = #{relaId,jdbcType=VARCHAR}



        and rela_content = #{relaContent,jdbcType=VARCHAR}


        and status = #{status,jdbcType=BIGINT}


        and create_person = #{createPerson,jdbcType=BIGINT}


        and create_time = #{createTime,jdbcType=TIMESTAMP}


        and update_person = #{updatePerson,jdbcType=BIGINT}


        and update_time = #{updateTime,jdbcType=TIMESTAMP}


        and del_flag = #{delFlag,jdbcType=BIGINT}

select rela_id as customerId, count(1) as quantity from live_room_rela where  rela_id in

        #{item}

        and rela_type = #{relaType}
group by rela_id
