package com.wanmi.sbc.returnorder.manualrefund.repository;import com.wanmi.sbc.returnorder.manualrefund.model.root.ManualRefundImg;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.JpaSpecificationExecutor;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface ManualRefundImgRepository extends JpaRepository<ManualRefundImg, Long>, JpaSpecificationExecutor<ManualRefundImg> {    List<ManualRefundImg> findManualRefundImgsByManualRefundImgIdIn(List<Long> manualRefundImgIdList);    @Query("from ManualRefundImg where delFlag =?1 and refundId=?2 and refundBillType=?3")    List<ManualRefundImg> findManualRefundImgsByRefundId(Integer delFlag, String refundId, Integer refundBillType);    List<ManualRefundImg> findManualRefundImgsByDelFlagAndRefundIdIn(Integer delFlag, List<String> refundIdList);    @Query("from ManualRefundImg where delFlag =0 ")    List<ManualRefundImg> findManualRefundImgsUnDelFlag();}